        // 4. Proses Insert ke Database
        foreach ($dataProdi as $prodiName => $assets) {
            // 1. Ambil Data Prodi dari DB untuk mendapatkan kode uniknya (IF, EL, MS, dll)
            $prodi = Prodi::where('name', 'LIKE', '%' . $prodiName . '%')->first();

            // Safety check
            if (!$prodi) {
                $this->command->warn("Prodi $prodiName tidak ditemukan di database. Skip aset.");
                continue;
            }

            // 2. Cari Lab milik Prodi tersebut
            $lab = Lab::where('prodi_id', $prodi->id)->first();

            // Fallback jika lab tidak ketemu
            if (!$lab) {
                $this->command->warn("Lab untuk prodi $prodiName tidak ditemukan. Skip aset.");
                continue;
            }

            foreach ($assets as $index => $item) {
                // 3. GENERATE KODE UNIK (Gunakan $prodi->code dari database)
                // Contoh: IF-001, EL-001, MS-001
                $uniqueCode = $prodi->code . '-' . str_pad($index + 1, 3, '0', STR_PAD_LEFT);

                DB::table('assets')->insert([
                    'name' => $item[0],
                    'code' => $uniqueCode, // Kode sudah pasti unik per prodi
                    'category_id' => $getCatId($item[0]),
                    'lab_id' => $lab->id,
                    'image' => $item[1],
                    'description' => "Aset Inventaris Resmi Program Studi $prodiName.",
                    'quantity' => rand(5, 15),
                    'status' => 'available',
                    'condition' => 'good',
                    'created_at' => now(),
                    'updated_at' => now(),
                ]);
            }
        }
